
import Rayconnect from './rayconnect';

console.log("starting ... ")
const rayconnect = new Rayconnect({
    scopes: "chat",
    appID: "rayGap",
    space: "main",
    type: "micros"
}, undefined, true)


rayconnect.socket.on('connect', () => {
    console.log('hiii')
    rayconnect.GetGuestAccess().then(async (data: any) => {


 
        console.log(data)

        const l = await rayconnect.CloudStorage.setItem("test", "test")
        console.log("local " , l)
        setTimeout(() => {
            rayconnect.changePermissions({
                mode: 'add',
                uid: 'guest',
                scope: 'chat',
                method: 'CHAT',
                address: `*`
            }).then((data) => {

    

                console.log(data)
                
            }).catch((e) => {
                console.log(e)
            })
        }, 5000);

        
    })
})



